import 'dart:math';import 'payment_gateway.dart';class MockPaymentGateway implements PaymentGateway{Future<PaymentResult> pay({required int amountRials,required String orderId,String? callbackUrl}) async{await Future.delayed(Duration(seconds:1));if(amountRials<=0)return PaymentResult.failure('Invalid amount');final code='TRK'+Random().nextInt(999999).toString().padLeft(6,'0');return PaymentResult.success(code);}}
