import 'package:flutter/material.dart';import 'package:mobile_scanner/mobile_scanner.dart';class ScannerPage extends StatefulWidget{const ScannerPage({super.key});@override State<ScannerPage> createState()=>_ScannerPageState();}class _ScannerPageState extends State<ScannerPage>{String? code;@override Widget build(BuildContext c){return Scaffold(appBar: AppBar(title: const Text('اسکن QR/بارکد')),body: Column(children:[Expanded(child: MobileScanner(onDetect:(cap){final b=cap.barcodes;if(b.isNotEmpty){setState(()=>code=b.first.rawValue);} })), if(code!=null) Padding(padding: const EdgeInsets.all(12), child: Text('کد: '+(code??''))),]));}}